    zone="de-fra1",
    plan="1xCPU-1GB",
    template={
        "storage": "Ubuntu Server 20.04 LTS (Focal Fossa)",
        "size": 25,
    },
    network_interfaces=[{
        "type": "public",
    }])
my_new_floating_address = upcloud.FloatingIpAddress("my_new_floating_address", mac_address=example.network_interfaces[0].mac_address)
